// API拦截器

// 获取认证token
export const getAuthToken = (): string | null => {
  if (typeof window !== 'undefined') {
    return localStorage.getItem('authToken')
  }
  return null
}

// 请求拦截器接口
export interface RequestInterceptorConfig {
  headers?: Record<string, string>
  requireAuth?: boolean
  timeout?: number
}

// 请求拦截器
export const requestInterceptor = async (
  url: string, 
  config: RequestInit & RequestInterceptorConfig
): Promise<RequestInit> => {
  const headers: Record<string, string> = { 
    ...config.headers 
  }
  
  // 只有当不是FormData时才设置Content-Type
  if (!(config.body instanceof FormData)) {
    headers['Content-Type'] = 'application/json'
  }
  
  // 如果需要认证，添加token
  if (config.requireAuth !== false) {
    const token = getAuthToken()
    if (token) {
      headers['Authorization'] = `Bearer ${token}`
    }
  }

  const requestConfig: RequestInit = {
    ...config,
    headers,
    signal: AbortSignal.timeout(config.timeout || 10000)
  }

  // 处理请求体
  if (config.body && config.method !== 'GET') {
    if (typeof config.body === 'object' && !(config.body instanceof FormData)) {
      requestConfig.body = JSON.stringify(config.body)
    }
  }

  return requestConfig
}

// 响应拦截器
export const responseInterceptor = async <T>(response: Response): Promise<T> => {
  let data: any

  try {
    const contentType = response.headers.get('content-type')
    if (contentType && contentType.includes('application/json')) {
      data = await response.json()
    } else {
      data = await response.text()
    }
  } catch (error) {
    throw new Error('响应解析失败')
  }

  // 处理HTTP错误状态
  if (!response.ok) {
    // 401 未授权 - 清除token并跳转登录
    if (response.status === 401) {
      if (typeof window !== 'undefined') {
        localStorage.removeItem('authToken')
        localStorage.removeItem('userInfo')
        // 如果不在登录页面，跳转到登录页面
        if (window.location.pathname !== '/auth/login') {
          window.location.href = '/auth/login'
        }
      }
    }

    // 抛出错误信息
    const errorMessage = data?.message || data?.error || `HTTP ${response.status}: ${response.statusText}`
    throw new Error(errorMessage)
  }

  return data
}

// 错误处理拦截器
export const errorInterceptor = (error: any, method: string, endpoint: string): never => {
  console.error(`API请求失败 [${method} ${endpoint}]:`, error)
  
  if (error instanceof Error) {
    if (error.name === 'TimeoutError') {
      throw new Error('请求超时，请检查网络连接')
    }
    if (error.name === 'TypeError' && error.message.includes('fetch')) {
      throw new Error('网络连接失败，请检查网络状态')
    }
    throw error
  }
  
  throw new Error('请求失败，请重试')
}

import { requestInterceptor, responseInterceptor, errorInterceptor, RequestInterceptorConfig } from './interceptors'
import type { ApiResponse } from '../types/api'

// API配置 - 根据环境确定API地址
const API_CONFIG = {
  BASE_URL: process.env.NODE_ENV === 'production' 
    ? 'https://api.xlcig.cn/api' 
    : 'http://' + (process.client ? window.location.hostname : 'localhost') + ':9999/api',
  TIMEOUT: 10000
}

// HTTP请求方法类型
type HttpMethod = 'GET' | 'POST' | 'PUT' | 'DELETE' | 'PATCH'

// API客户端类
class ApiClient {
  private baseURL: string
  private timeout: number

  constructor() {
    this.baseURL = API_CONFIG.BASE_URL
    this.timeout = API_CONFIG.TIMEOUT
  }

  // 统一请求方法
  async request<T = any>(
    endpoint: string, 
    config: RequestInit & RequestInterceptorConfig = {}
  ): Promise<ApiResponse<T>> {
    const url = `${this.baseURL}${endpoint.startsWith('/') ? endpoint : '/' + endpoint}`
    const method = (config.method || 'GET') as HttpMethod
    
    try {
      // 应用请求拦截器
      const requestConfig = await requestInterceptor(url, {
        timeout: this.timeout,
        ...config
      })
      // 发送请求
      const response = await fetch(url, requestConfig)
      // 应用响应拦截器
      return await responseInterceptor<ApiResponse<T>>(response)
    } catch (error) {
      // 应用错误拦截器
      return errorInterceptor(error, method, endpoint)
    }
  }

  // GET请求
  async get<T = any>(
    endpoint: string, 
    config?: Omit<RequestInterceptorConfig, 'method'>
  ): Promise<ApiResponse<T>> {
    return this.request<T>(endpoint, { ...config, method: 'GET' })
  }

  // POST请求
  async post<T = any>(
    endpoint: string, 
    body?: any, 
    config?: Omit<RequestInterceptorConfig, 'method' | 'body'>
  ): Promise<ApiResponse<T>> {
    return this.request<T>(endpoint, { ...config, method: 'POST', body })
  }

  // PUT请求
  async put<T = any>(
    endpoint: string, 
    body?: any, 
    config?: Omit<RequestInterceptorConfig, 'method' | 'body'>
  ): Promise<ApiResponse<T>> {
    return this.request<T>(endpoint, { ...config, method: 'PUT', body })
  }

  // DELETE请求
  async delete<T = any>(
    endpoint: string, 
    config?: Omit<RequestInterceptorConfig, 'method'>
  ): Promise<ApiResponse<T>> {
    return this.request<T>(endpoint, { ...config, method: 'DELETE' })
  }

  // PATCH请求
  async patch<T = any>(
    endpoint: string, 
    body?: any, 
    config?: Omit<RequestInterceptorConfig, 'method' | 'body'>
  ): Promise<ApiResponse<T>> {
    return this.request<T>(endpoint, { ...config, method: 'PATCH', body })
  }

  // 带查询参数的GET请求
  async getWithParams<T = any>(
    endpoint: string,
    params?: Record<string, any>,
    config?: Omit<RequestInterceptorConfig, 'method'>
  ): Promise<ApiResponse<T>> {
    let url = endpoint
    
    if (params) {
      const searchParams = new URLSearchParams()
      Object.entries(params).forEach(([key, value]) => {
        if (value !== undefined && value !== null && value !== '') {
          if (Array.isArray(value)) {
            value.forEach(item => searchParams.append(key, String(item)))
          } else {
            searchParams.append(key, String(value))
          }
        }
      })
      
      if (searchParams.toString()) {
        url += `?${searchParams.toString()}`
      }
    }
    
    return this.get<T>(url, config)
  }

  // 上传文件
  async upload<T = any>(
    endpoint: string,
    formData: FormData,
    config?: Omit<RequestInterceptorConfig, 'method' | 'body'>
  ): Promise<ApiResponse<T>> {
    return this.request<T>(endpoint, {
      ...config,
      method: 'POST',
      body: formData,
      headers: {
        // 不设置Content-Type，让浏览器自动设置multipart/form-data
        ...(config?.headers || {})
      }
    })
  }
}

// 创建并导出API客户端实例
export const apiClient = new ApiClient()
export default apiClient
