import { apiClient } from '../apiClient'
import type {
  User,
  UserCreateInput,
  UserLoginInput,
  UserLoginResponse,
  UserUpdateInput,
  PasswordChangeInput,
  ApiResponse
} from '../../types/api'

// ËÆ§ËØÅÁõ∏ÂÖ≥API
export const authApi = {
  // Áî®Êà∑Ê≥®ÂÜå
  register: (userData: UserCreateInput): Promise<ApiResponse<User>> =>
    apiClient.post('/auth/register', userData, { requireAuth: false }),

  // Áî®Êà∑ÁôªÂΩï
  login: (credentials: UserLoginInput): Promise<ApiResponse<UserLoginResponse>> =>
    apiClient.post('/auth/login', credentials, { requireAuth: false }),

  // Ëé∑ÂèñÂΩìÂâçÁî®Êà∑‰ø°ÊÅØ
  getProfile: (): Promise<ApiResponse<User>> =>
    apiClient.get('/auth/me'),

  // Êõ¥Êñ∞Áî®Êà∑‰ø°ÊÅØ
  updateProfile: (userData: UserUpdateInput): Promise<ApiResponse<User>> =>
    apiClient.put('/auth/profile', userData),

  // ‰øÆÊîπÂØÜÁ†Å
  changePassword: (passwordData: PasswordChangeInput): Promise<ApiResponse<void>> =>
    apiClient.put('/auth/password', passwordData),

  // Ëé∑ÂèñÁî®Êà∑ÂàóË°®ÔºàÁÆ°ÁêÜÂëòÂäüËÉΩÔºâ
  getUsers: (): Promise<ApiResponse<User[]>> =>
    apiClient.get('/users'),

  // Âà∑Êñ∞Token
  refreshToken: (refreshToken: string): Promise<ApiResponse<{ token: string }>> =>
    apiClient.post('/auth/refresh', { refreshToken }, { requireAuth: false }),

  // ÁôªÂá∫
  logout: (): Promise<ApiResponse<void>> =>
    apiClient.post('/auth/logout'),

  // ÂøòËÆ∞ÂØÜÁ†Å
  forgotPassword: (email: string): Promise<ApiResponse<void>> =>
    apiClient.post('/auth/forgot-password', { email }, { requireAuth: false }),

  // ÈáçÁΩÆÂØÜÁ†Å
  resetPassword: (token: string, newPassword: string): Promise<ApiResponse<void>> =>
    apiClient.post('/auth/reset-password', { token, newPassword }, { requireAuth: false }),

  // È™åËØÅÈÇÆÁÆ±
  verifyEmail: (token: string): Promise<ApiResponse<void>> =>
    apiClient.post('/auth/verify-email', { token }, { requireAuth: false }),

  // ÈáçÊñ∞ÂèëÈÄÅÈ™åËØÅÈÇÆ‰ª∂
  resendVerification: (email: string): Promise<ApiResponse<void>> =>
    apiClient.post('/auth/resend-verification', { email }, { requireAuth: false }),

  // ÂèëÈÄÅÈÇÆÁÆ±È™åËØÅÁ†Å
  sendEmailCode: async (data: { email: string }): Promise<ApiResponse<{ message: string }>> => {
    // Ê£ÄÊü•ÂèëÈÄÅÈ¢ëÁéáÈôêÂà∂Ôºà60ÁßíÂÜÖÂè™ËÉΩÂèëÈÄÅ‰∏ÄÊ¨°Ôºâ
    const lastSendKey = `lastSend_${data.email}`
    const lastSendTime = localStorage.getItem(lastSendKey)
    if (lastSendTime && Date.now() - parseInt(lastSendTime) < 60000) {
      return {
        success: false,
        message: 'ÂèëÈÄÅËøá‰∫éÈ¢ëÁπÅÔºåËØ∑60ÁßíÂêéÂÜçËØï'
      }
    }

    // ÁîüÊàê6‰ΩçÊï∞Â≠óÈ™åËØÅÁ†Å
    const code = Math.floor(100000 + Math.random() * 900000).toString()
    const messageHtml = `
      <!DOCTYPE html>
      <html>
      <head>
        <meta charset="UTF-8">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
        <title>xlCig - ÈÇÆÁÆ±È™åËØÅ</title>
      </head>
      <body style="margin: 0; padding: 20px; background-color: #0a0a0a; font-family: Arial, sans-serif;">
        <div style="max-width: 480px; margin: 0 auto; background: #1a1a1a; border-radius: 8px; border: 1px solid #333; overflow: hidden;">
          
          <!-- Header -->
          <div style="background: #000000; padding: 24px 20px; text-align: center; border-bottom: 1px solid #333;">
            <div style="margin-bottom: 12px;">
              <!-- Logo -->
              <img src="https://xlcig.oss-cn-beijing.aliyuncs.com/avatars/logo.png?Expires=1751639237&OSSAccessKeyId=TMP.3KmZD6t8o3n1rKuSyCorjaftjmjgbsmLYDq74CzmdgC4XQGqBuLsum69r8snmQ2jFs2XMgB46rvT87S3VB9otvmzTpL3fB&Signature=Oax6b3GJVc%2BpH9f69iGgtpTbQdg%3D" alt="xlCig" style="height: 40px; max-width: 160px; display: inline-block;">
            </div>
            <p style="color: #888888; margin: 0; font-size: 12px; text-transform: uppercase; letter-spacing: 1px;">
              ÈÇÆÁÆ±È™åËØÅ
            </p>
          </div>

          <!-- Main Content -->
          <div style="padding: 24px 20px; background: #1a1a1a;">
            
            <!-- Verification Code -->
            <div style="text-align: center; margin-bottom: 20px;">
              <h2 style="color: #ffffff; margin: 0 0 8px 0; font-size: 18px; font-weight: 600;">
                È™åËØÅÁ†Å
              </h2>
              <p style="color: #888888; font-size: 13px; margin: 0 0 16px 0;">
                ËØ∑ËæìÂÖ•‰ª•‰∏ãÈ™åËØÅÁ†ÅÂÆåÊàêÊ≥®ÂÜå
              </p>
              
              <div style="background: #000000; border: 1px solid #333; border-radius: 6px; padding: 20px; margin: 16px 0;">
                <div style="font-size: 28px; font-weight: bold; color: #00f5ff; letter-spacing: 4px; font-family: 'Courier New', monospace;">
                  ${code}
                </div>
                <div style="margin-top: 8px; padding-top: 8px; border-top: 1px solid #333;">
                  <span style="color: #888888; font-size: 11px;">
                    ÊúâÊïàÊúüÔºö10ÂàÜÈíü
                  </span>
                </div>
              </div>
            </div>

            <!-- Security Notice -->
            <div style="background: #111111; border: 1px solid #333; border-radius: 4px; padding: 16px; margin: 16px 0;">
              <p style="color: #ffffff; font-size: 12px; font-weight: bold; margin: 0 0 6px 0;">
                üõ°Ô∏è ÂÆâÂÖ®ÊèêÈÜí
              </p>
              <p style="color: #cccccc; font-size: 11px; margin: 0; line-height: 1.4;">
                ‚Ä¢ ËØ∑ÂãøÂêë‰ªª‰Ωï‰∫∫ÈÄèÈú≤Ê≠§È™åËØÅÁ†Å<br>
                ‚Ä¢ Â¶ÇÈùûÊú¨‰∫∫Êìç‰ΩúÔºåËØ∑ÂøΩÁï•Ê≠§ÈÇÆ‰ª∂
              </p>
            </div>

          </div>

          <!-- Footer -->
          <div style="background: #000000; border-top: 1px solid #333; padding: 16px 20px; text-align: center;">
            <p style="color: #666666; font-size: 10px; margin: 0; line-height: 1.3;">
              Ê≠§ÈÇÆ‰ª∂Áî± xlCig Á≥ªÁªüÂèëÈÄÅ<br>
              <span style="color: #888888;">¬© 2025 xlCig</span>
            </p>
          </div>
        </div>
      </body>
      </html>`
    //ÁΩëÊòìÊéàÊùÉÁ†Å
    const wyKey = 'QUedTyAvtaBLqjjm'
    //qqÊéàÊùÉÁ†Å
    const qqKey = 'odmyaacplscmdfgi'
    //apiKey
    const apiKey = 'kQkGSVy2vEnsoX1751441583687rSR1yklfuc'

    // Ê†πÊçÆÁî®Êà∑ÈÇÆÁÆ±ÂêéÁºÄÈÄâÊã©ÂØπÂ∫îÁöÑÊéàÊùÉÁ†ÅÂíåÈÇÆÁÆ±Á±ªÂûã
    const emailDomain = data.email.split('@')[1]?.toLowerCase()
    let smtpCode = ''
    let smtpEmail = ''
    let smtpCodeType = ''

    if (emailDomain === '163.com' || emailDomain === '126.com') {
      smtpCode = wyKey
      smtpEmail = '18569795073@163.com' // ÊõøÊç¢‰∏∫ÂÆûÈôÖÁöÑ163ÈÇÆÁÆ±
      smtpCodeType = emailDomain === '163.com' ? '163' : '126'
    } else if (emailDomain === 'qq.com') {
      smtpCode = qqKey
      smtpEmail = '2966898893@qq.com' // ÊõøÊç¢‰∏∫ÂÆûÈôÖÁöÑQQÈÇÆÁÆ±
      smtpCodeType = 'qq'
    } else {
      // ÈªòËÆ§‰ΩøÁî®163
      smtpCode = wyKey
      smtpEmail = '18569795073@163.com'
      smtpCodeType = '163'
    }

    // ÈÇÆ‰ª∂ËØ∑Ê±ÇÁöÑÂÖ±ÂêåÂèÇÊï∞ÈÖçÁΩÆ
    const mailRequestData = {
      ColaKey: apiKey,           // APIÂØÜÈí•
      tomail: data.email,        // ÁõÆÊ†áÈÇÆÁÆ±Âú∞ÂùÄ
      fromTitle:"xlCig",      // ÈÇÆ‰ª∂Ê†áÈ¢òÔºàÂèë‰ª∂‰∫∫ÂêçÁß∞Ôºâ
      subject: 'register is to xlCig',  // ÈÇÆ‰ª∂‰∏ªÈ¢ò
      content: messageHtml.trim(), // ÈÇÆ‰ª∂ÂÜÖÂÆπ(html)
      isTextContent: false,      // ÈÇÆ‰ª∂ÂÜÖÂÆπÊòØHTMLÊ†ºÂºè
      smtpCode: smtpCode,        // ÈÇÆ‰ª∂Á≥ªÁªüÊéàÊùÉÁ†Å
      smtpEmail: smtpEmail,      // ÂºÄÂêØÊéàÊùÉÁ†ÅÂØπÂ∫îÁöÑÊéàÊùÉÈÇÆÁÆ±
      smtpCodeType: smtpCodeType // ÊéàÊùÉÈÇÆÁÆ±ÁöÑÁ±ªÂûã
    }

    try {
      // Ë∞ÉÁî®Â§ñÈÉ®ÈÇÆ‰ª∂ÊúçÂä°API
      const mailResponse = await fetch('https://luckycola.com.cn/tools/customMail', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(mailRequestData)
      })

      if (!mailResponse.ok) {
        const errorData = await mailResponse.text()
        console.error('ÈÇÆ‰ª∂ÊúçÂä°ÂìçÂ∫îÈîôËØØ:', errorData)
        throw new Error('ÈÇÆ‰ª∂ÂèëÈÄÅÂ§±Ë¥•')
      }

      // ËÆ∞ÂΩïÂèëÈÄÅÊó∂Èó¥
      localStorage.setItem(lastSendKey, Date.now().toString())

      // Â∞ÜÈ™åËØÅÁ†Å‰∏¥Êó∂Â≠òÂÇ®Âà∞ localStorageÔºåÂÆûÈôÖÈ°πÁõÆ‰∏≠Â∫îËØ•Â≠òÂÇ®Âà∞ÂêéÁ´Ø
      const codeData = {
        email: data.email,
        code: code,
        timestamp: Date.now(),
        expires: Date.now() + 10 * 60 * 1000 // 10ÂàÜÈíüËøáÊúü
      }
      localStorage.setItem(`emailCode_${data.email}`, JSON.stringify(codeData))

      return {
        success: true,
        message: 'È™åËØÅÁ†ÅÂèëÈÄÅÊàêÂäüÔºåËØ∑Êü•ÁúãÊÇ®ÁöÑÈÇÆÁÆ±',
        data: { message: 'È™åËØÅÁ†ÅÂ∑≤ÂèëÈÄÅ' }
      }
    } catch (error) {
      console.error('ÂèëÈÄÅÈÇÆÁÆ±È™åËØÅÁ†ÅÂ§±Ë¥•:', error)
      // Â¶ÇÊûúHTTPSÂ§±Ë¥•ÔºåÂ∞ùËØïHTTP
      try {
        const httpResponse = await fetch('http://luckycola.com.cn/tools/customMail', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(mailRequestData)
        })

        if (httpResponse.ok) {
          // ËÆ∞ÂΩïÂèëÈÄÅÊó∂Èó¥
          localStorage.setItem(lastSendKey, Date.now().toString())
          
          return {
            success: true,
            message: 'È™åËØÅÁ†ÅÂèëÈÄÅÊàêÂäüÔºåËØ∑Êü•ÁúãÊÇ®ÁöÑÈÇÆÁÆ±',
            data: { message: 'È™åËØÅÁ†ÅÂ∑≤ÂèëÈÄÅ' }
          }
        }
      } catch (httpError) {
        console.error('HTTPÈÇÆ‰ª∂ÂèëÈÄÅ‰πüÂ§±Ë¥•:', httpError)
      }

      return {
        success: false,
        message: error instanceof Error ? error.message : 'ÂèëÈÄÅÈ™åËØÅÁ†ÅÂ§±Ë¥•ÔºåËØ∑Á®çÂêéÈáçËØï'
      }
    }
  },

  // È™åËØÅÈÇÆÁÆ±È™åËØÅÁ†Å
  verifyEmailCode: (data: { email: string; code: string }): Promise<ApiResponse<boolean>> => {
    try {
      const storedData = localStorage.getItem(`emailCode_${data.email}`)
      if (!storedData) {
        return Promise.resolve({
          success: false,
          message: 'È™åËØÅÁ†Å‰∏çÂ≠òÂú®ÊàñÂ∑≤ËøáÊúü'
        })
      }

      const codeInfo = JSON.parse(storedData)
      const now = Date.now()

      if (now > codeInfo.expires) {
        localStorage.removeItem(`emailCode_${data.email}`)
        return Promise.resolve({
          success: false,
          message: 'È™åËØÅÁ†ÅÂ∑≤ËøáÊúüÔºåËØ∑ÈáçÊñ∞Ëé∑Âèñ'
        })
      }

      if (codeInfo.code !== data.code) {
        return Promise.resolve({
          success: false,
          message: 'È™åËØÅÁ†ÅÈîôËØØ'
        })
      }

      // È™åËØÅÊàêÂäüÂêéÂà†Èô§È™åËØÅÁ†Å
      localStorage.removeItem(`emailCode_${data.email}`)

      return Promise.resolve({
        success: true,
        message: 'È™åËØÅÁ†ÅÈ™åËØÅÊàêÂäü',
        data: true
      })
    } catch (error) {
      return Promise.resolve({
        success: false,
        message: 'È™åËØÅÁ†ÅÈ™åËØÅÂ§±Ë¥•'
      })
    }
  }
}

export default authApi

import { apiClient } from '../apiClient'
import type { 
  ProductCategory, 
  CategoryCreateInput,
  PaginationParams,
  ApiResponse 
} from '../../types/api'

// ÂàÜÁ±ªÊü•ËØ¢ÂèÇÊï∞
export interface CategoryQueryParams extends PaginationParams {
  status?: 'active' | 'inactive'
  search?: string
}

// ‰∫ßÂìÅÂàÜÁ±ªÁõ∏ÂÖ≥API
export const categoriesApi = {
  // Ëé∑ÂèñÂàÜÁ±ªÂàóË°®
  getCategories: (params?: CategoryQueryParams): Promise<ApiResponse<ProductCategory[]>> => 
    apiClient.getWithParams('/categories', params, { requireAuth: false }),

  // Ëé∑ÂèñÊâÄÊúâÊøÄÊ¥ªÁöÑÂàÜÁ±ªÔºà‰∏çÂàÜÈ°µÔºâ
  getActiveCategories: (): Promise<ApiResponse<ProductCategory[]>> => 
    apiClient.get('/categories/active', { requireAuth: false }),

  // Ê†πÊçÆIDËé∑ÂèñÂàÜÁ±ªËØ¶ÊÉÖ
  getCategoryById: (id: number): Promise<ApiResponse<ProductCategory>> => 
    apiClient.get(`/categories/${id}`, { requireAuth: false }),

  // Ê†πÊçÆslugËé∑ÂèñÂàÜÁ±ªËØ¶ÊÉÖ
  getCategoryBySlug: (slug: string): Promise<ApiResponse<ProductCategory>> => 
    apiClient.get(`/categories/slug/${slug}`, { requireAuth: false }),

  // ÂàõÂª∫ÂàÜÁ±ªÔºàÁÆ°ÁêÜÂëòÂäüËÉΩÔºâ
  createCategory: (categoryData: CategoryCreateInput): Promise<ApiResponse<ProductCategory>> => 
    apiClient.post('/categories', categoryData),

  // Êõ¥Êñ∞ÂàÜÁ±ªÔºàÁÆ°ÁêÜÂëòÂäüËÉΩÔºâ
  updateCategory: (id: number, categoryData: Partial<CategoryCreateInput>): Promise<ApiResponse<ProductCategory>> => 
    apiClient.put(`/categories/${id}`, categoryData),

  // Âà†Èô§ÂàÜÁ±ªÔºàÁÆ°ÁêÜÂëòÂäüËÉΩÔºâ
  deleteCategory: (id: number): Promise<ApiResponse<void>> => 
    apiClient.delete(`/categories/${id}`),

  // ÊâπÈáèÂà†Èô§ÂàÜÁ±ªÔºàÁÆ°ÁêÜÂëòÂäüËÉΩÔºâ
  batchDeleteCategories: (ids: number[]): Promise<ApiResponse<void>> => 
    apiClient.post('/categories/batch-delete', { ids }),

  // Êõ¥Êñ∞ÂàÜÁ±ªÁä∂ÊÄÅÔºàÁÆ°ÁêÜÂëòÂäüËÉΩÔºâ
  updateCategoryStatus: (id: number, status: 'active' | 'inactive'): Promise<ApiResponse<ProductCategory>> => 
    apiClient.patch(`/categories/${id}/status`, { status }),

  // Êõ¥Êñ∞ÂàÜÁ±ªÊéíÂ∫èÔºàÁÆ°ÁêÜÂëòÂäüËÉΩÔºâ
  updateCategoryOrder: (orders: { id: number; sort_order: number }[]): Promise<ApiResponse<void>> => 
    apiClient.post('/categories/update-order', { orders }),

  // Ëé∑ÂèñÂàÜÁ±ªÁªüËÆ°‰ø°ÊÅØÔºàÁÆ°ÁêÜÂëòÂäüËÉΩÔºâ
  getCategoryStats: (): Promise<ApiResponse<{ 
    total: number; 
    active: number; 
    inactive: number 
  }>> => 
    apiClient.get('/categories/stats')
}

export default categoriesApi

import { apiClient } from '../apiClient'
import type { 
  ComputerConfig, 
  ConfigCreateInput,
  ConfigQueryParams,
  ApiResponse 
} from '../../types/api'

// ÁîµËÑëÈÖçÁΩÆÁõ∏ÂÖ≥API
export const configsApi = {
  // Ëé∑ÂèñÈÖçÁΩÆÂàóË°®
  getConfigs: (params?: ConfigQueryParams): Promise<ApiResponse<ComputerConfig[]>> => 
    apiClient.getWithParams('/configs', params, { requireAuth: false }),

  // Ëé∑ÂèñÊé®ËçêÈÖçÁΩÆ
  getFeaturedConfigs: (limit: number = 10): Promise<ApiResponse<ComputerConfig[]>> => 
    apiClient.get(`/configs/featured?limit=${limit}`, { requireAuth: false }),

  // Ëé∑ÂèñÁÉ≠Èó®ÈÖçÁΩÆ
  getHotConfigs: (limit: number = 10): Promise<ApiResponse<ComputerConfig[]>> => 
    apiClient.get(`/configs/hot?limit=${limit}`, { requireAuth: false }),

  // Ëé∑ÂèñÊúÄÊñ∞ÈÖçÁΩÆ
  getLatestConfigs: (limit: number = 10): Promise<ApiResponse<ComputerConfig[]>> => 
    apiClient.get(`/configs/latest?limit=${limit}`, { requireAuth: false }),

  // Ê†πÊçÆÂàÜÁ±ªËé∑ÂèñÈÖçÁΩÆ
  getConfigsByCategory: (category: string, params?: Omit<ConfigQueryParams, 'category'>): Promise<ApiResponse<ComputerConfig[]>> => 
    apiClient.getWithParams(`/configs/category/${category}`, params, { requireAuth: false }),

  // Ê†πÊçÆÂïÜÂÆ∂Ëé∑ÂèñÈÖçÁΩÆ
  getConfigsByMerchant: (merchantId: number, params?: Omit<ConfigQueryParams, 'merchant_id'>): Promise<ApiResponse<ComputerConfig[]>> => 
    apiClient.getWithParams(`/configs/merchant/${merchantId}`, params, { requireAuth: false }),

  // Ê†πÊçÆ‰ª∑Ê†ºÂå∫Èó¥Ëé∑ÂèñÈÖçÁΩÆ
  getConfigsByPrice: (minPrice: number, maxPrice: number, params?: Omit<ConfigQueryParams, 'min_price' | 'max_price'>): Promise<ApiResponse<ComputerConfig[]>> => 
    apiClient.getWithParams('/configs/price-range', { min_price: minPrice, max_price: maxPrice, ...params }, { requireAuth: false }),

  // Ê†πÊçÆIDËé∑ÂèñÈÖçÁΩÆËØ¶ÊÉÖ
  getConfigById: (id: number): Promise<ApiResponse<ComputerConfig>> => 
    apiClient.get(`/configs/${id}`, { requireAuth: false }),

  // Ê†πÊçÆslugËé∑ÂèñÈÖçÁΩÆËØ¶ÊÉÖ
  getConfigBySlug: (slug: string): Promise<ApiResponse<ComputerConfig>> => 
    apiClient.get(`/configs/slug/${slug}`, { requireAuth: false }),

  // ÊêúÁ¥¢ÈÖçÁΩÆ
  searchConfigs: (query: string, params?: Omit<ConfigQueryParams, 'search'>): Promise<ApiResponse<ComputerConfig[]>> => 
    apiClient.getWithParams('/configs/search', { search: query, ...params }, { requireAuth: false }),

  // ÈÖçÁΩÆÂØπÊØî
  compareConfigs: (configIds: number[]): Promise<ApiResponse<ComputerConfig[]>> => 
    apiClient.post('/configs/compare', { configIds }, { requireAuth: false }),

  // Ëé∑ÂèñÁõ∏‰ººÈÖçÁΩÆ
  getSimilarConfigs: (configId: number, limit: number = 6): Promise<ApiResponse<ComputerConfig[]>> => 
    apiClient.get(`/configs/${configId}/similar?limit=${limit}`, { requireAuth: false }),

  // Ëé∑ÂèñÈÖçÁΩÆÊé®ËçêÔºàÂü∫‰∫éÈ¢ÑÁÆóÔºâ
  getRecommendedConfigs: (budget: number, category?: string): Promise<ApiResponse<ComputerConfig[]>> => 
    apiClient.getWithParams('/configs/recommend', { budget, category }, { requireAuth: false }),

  // Ëé∑ÂèñÈÖçÁΩÆÊÄßËÉΩËØÑÂàÜËØ¶ÊÉÖ
  getConfigPerformance: (configId: number): Promise<ApiResponse<{
    cpu_score: number
    gpu_score: number
    memory_score: number
    storage_score: number
    overall_score: number
    gaming_performance: number
    work_performance: number
  }>> => 
    apiClient.get(`/configs/${configId}/performance`, { requireAuth: false }),

  // Ëé∑ÂèñÈÖçÁΩÆ‰ª∑Ê†ºÂéÜÂè≤
  getConfigPriceHistory: (configId: number, days: number = 30): Promise<ApiResponse<{ date: string; price: number }[]>> => 
    apiClient.get(`/configs/${configId}/price-history?days=${days}`, { requireAuth: false }),

  // ÂàõÂª∫ÈÖçÁΩÆÔºàÂïÜÂÆ∂ÂäüËÉΩÔºâ
