export const uploadApi = {
  // ‰∏ä‰º†Â§¥ÂÉè
  uploadAvatar: async (file: File): Promise<ApiResponse<{ avatar: string; user: User }>> => {
    const formData = new FormData()
    formData.append('avatar', file)

    return apiClient.upload('/upload/avatar', formData)
  },

  // È™åËØÅÂõæÁâáÊñá‰ª∂
  validateImageFile: (file: File): { valid: boolean; message?: string } => {
    // Ê£ÄÊü•Êñá‰ª∂Á±ªÂûã
    const allowedTypes = ['image/jpeg', 'image/jpg', 'image/png', 'image/gif', 'image/webp']
    if (!allowedTypes.includes(file.type)) {
      return {
        valid: false,
        message: 'Âè™ÊîØÊåÅ JPEG„ÄÅPNG„ÄÅGIF„ÄÅWebP Ê†ºÂºèÁöÑÂõæÁâáÊñá‰ª∂'
      }
    }

    // Ê£ÄÊü•Êñá‰ª∂Â§ßÂ∞è (2MB)
    const maxSize = 2 * 1024 * 1024
    if (file.size > maxSize) {
      return {
        valid: false,
        message: 'Êñá‰ª∂Â§ßÂ∞è‰∏çËÉΩË∂ÖËøá2MB'
      }
    }

    return { valid: true }
  },

  // Ëé∑ÂèñÊñá‰ª∂È¢ÑËßàURL
  getFilePreviewUrl: (file: File): string => {
    return URL.createObjectURL(file)
  },

  // ÈáäÊîæÊñá‰ª∂È¢ÑËßàURL
  releaseFilePreviewUrl: (url: string): void => {
    URL.revokeObjectURL(url)
  }
}

